INSERT INTO `persyaratan` (`id`, `jenis`) VALUES
(1, 'Pria/wanita, bukan anggota/mantan prajurit TNI/Polri.'),
(2, 'Berijazah SMA/MA program IPA/IPS, dengan ketentuan nilai NEM/UAN sebagai berikut:'),
(3, 'Pria jurusan IPA Lulusan Tahun 2012, Lulus Ujian Nasional dengan nilai rata-rata (gabungan nilai UN dan nilai ujian sekolah) minimal 7,25  dan tidak ada nilai di bawah 6 di dalam kolom nilai akhir;'),
(4, 'Pria jurusan IPA Lulusan tahun 2015, Lulus Ujian Akhir Nasional dengan nilai rata-rata minimal 60,00 dan tidak ada nilai mata pelajaran di bawah 40,00 di dalam kolom nilai UN.'),
(5, 'Pria jurusan IPA Lulusan tahun 2016, Lulus Ujian Akhir Nasional dengan nilai rata-rata minimal 55,00 dan tidak ada nilai mata pelajaran di bawah 50,00 di dalam kolom nilai UN.'),
(6, 'bagi Wanita hanya jurusan IPA Lulusan tahun 2017 di wilayah Jawa dan Sumatera.'),
(7, 'Lulusan tahun 2017 akan ditentukan kemudian.'),
(8, 'Memiliki tinggi badan sekurang-kurangnya 163 cm untuk pria dan 157 cm untuk wanita serta memiliki berat badan seimbang menurut ketentuan yang berlaku.'),
(9, 'Harus mengikuti pemeriksaaan/pengujian yang diselenggarakan oleh panitia penerimaan yang meliputi :(\r\nAdministrasi,Kesehatan,Jasmani,Mental Ideologi,Psikologi dan Akademik.');

